    [Parameter]
    public int id { get; set; }
    Noticie noticia = new Noticie();
    public bool publicada { get; set; }

    protected async override Task OnInitializedAsync()
    {
        try
        {
            noticia = await NoticeServices.GetNotice(id);
            publicada = noticia.Publicado;

        }
        catch (Exception)
        {

            throw;
        }
    }

    protected async Task Ocultar()
    {
        publicada = false;
        noticia.Publicado = false;
        noticia.FechaPublicacion = DateTime.Now.Date;
        await NoticeServices.PublicNotice(noticia);
    }


